import React from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import { ButtonDropdown, DropdownToggle, Button } from 'reactstrap';

class Acceuil extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      clubs: []
    };
  };

  componentDidMount() {
    axios
      .get('/user/clubs')
      .then(res => res.data)
      .then(result =>
        this.setState({
          clubs: result
        })
      );
  };

  render() {
    return (
      <div className="acceuil">
        <h1>LA BOXE EST AUSSI UN SPORT DE FEMME !</h1>
        <Button color="danger" className="button-acceuil">Référence ton club</Button>
        <ButtonDropdown >
          <DropdownToggle caret>
            Choisis un club
      </DropdownToggle>

          {/* <DropdownMenu>
