import React from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Form, FormGroup, Label, Input, Button } from 'reactstrap';
import './reference.css';

class Reference extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      name: null,
      phone_number: null,
      email: '',
      female_member: null,
      male_member: null,
      monthly_price: null,
      annual_price: null,
      description: '',
      districts: [],
      Manager_id_manager: 1
    };
    this.handleSubmitClub = this.handleSubmitClub.bind(this);
    this.handleChange = this.handleChange.bind(this);
  };

  handleChange(event) {
    this.setState({ name: event.target.value, Manager_id_manager: event.target.id });
  };

  async handleSubmitClub(event) {
    event.preventDefault();
    const { Manager_id_manager } = this.state;
    await axios
      .post(`/boxingirl/clubs/${Manager_id_manager}`, {
        Manager_id_manager
      });
  };

  render() {
    return (
      <form onSubmit={this.handleSubmitClub} className="form-reference">
        <h1 className="reference-title">Référence ta team !</h1>
        <Form>
          <FormGroup>
            <Label for="exampleEmail">Nom</Label>
            <Input type="text" name="email" id="name" onChange={this.handleChange} />
          </FormGroup>
          <FormGroup>
            <Label for="exampleEmail">Numéro de téléphone</Label>
            <Input type="number" name="phone_number" id="phone_number" placeholder="Renseigner le téléphone du club" />
          </FormGroup>
          <FormGroup>
            <Label for="exampleEmail">Email</Label>
            <Input type="email" name="email" id="email" placeholder="Saisissez votre email" />
          </FormGroup>
          <FormGroup>
            <Label for="exampleSelect">Quartiers</Label>
            <Input type="select" name="selectMulti" id="distric">
              <option>1</option>
              <option>2</option>
              <option>3</option>
            </Input>
          </FormGroup>
          <FormGroup>
            <Label for="subscribtion">Prix abonnement mensuel</Label>
            <Input type="number" name="email" id="monthly_price" placeholder="Saisissez votre email" />
          </FormGroup>
          <FormGroup>
            <Label for="subscription">Prix abonnement annuel</Label>
            <Input type="number" name="email" id="annual_price" placeholder="Saisissez votre email" />
          </FormGroup>
          <FormGroup>
            <Label for="men">Nombre de hommes licenciés</Label>
            <Input type="number" name="men" id="male_member" placeholder="Renseignez un nombre" />
          </FormGroup>
          <FormGroup>
            <Label for="women">Nombre de femmes licenciées</Label>
            <Input type="number" name="women" id="women_member" placeholder="Renseignez un nombre" />
          </FormGroup>
          <FormGroup>
            <Label for="exampleText">Décrivez votre club</Label>
            <Input type="textarea" name="description" id="description" />
          </FormGroup>
          <Button>Envoyer</Button>
        </Form>
      </form>
    )
  }
};

export default Reference;
